function PopulateLetterToggles(){PopulateVowels();PopulateConsonants()}function PopulateConsonants(){var e=$("#consonants_container_0");var t=1;e.empty();for(var n=0;n<mConsonants.length;n++){if(n!=0&&n%7==0){var r="consonants_countainer_"+t;var i="<br/><br/><div id='"+r+"' class='btn-group' data-toggle='buttons'></div>";e.after(i);t++;e=$("#"+r)}AppendToggleToContainer(mConsonants[n],e)}}function PopulateVowels(){var e=$("#vowels_container");e.empty();for(var t=0;t<mVowels.length;t++){AppendToggleToContainer(mVowels[t],e)}}function AppendToggleToContainer(e,t){var n='<label id="'+e+'" class="letter-toggle btn btn-primary">'+'<input type="checkbox"> '+e+"</label>";if(mDebug){console.log("About to append the following: "+n)}t.append(n)}function SelectRandomToggles(){var e=$("#vowels_container .letter-toggle");TogglePercentageOfToggles(.85,e);var t=$("#consonants_outer_container .letter-toggle");TogglePercentageOfToggles(.2,t)}function TogglePercentageOfToggles(e,t){t.each(function(){if(Math.random()>1-e){if($(this).hasClass("active")){$(this).removeClass("active")}else{$(this).addClass("active")}}})}function GenerateWord(){var e=$("#vowels_container .letter-toggle.active");var t=$("#consonants_outer_container .letter-toggle.active");var n=[];e.each(function(){n.push($(this).attr("id"))});var r=[];t.each(function(){r.push($(this).attr("id"))});var i=$("#length_slider").val();if(mDebug){console.log("temere.js -- GenerateWord():: Active vowels = "+n);console.log("temere.js -- GenerateWord():: Active consonants = "+r);console.log("temere.js -- GenerateWord():: Length = "+i)}var s="";if(r.length>0&&n.length>0){s=BuildWord(n,r,i)}else{s=":("}$("#generated_word").text(s)}function BuildWord(e,t,n){if(mDebug){console.log("Building "+n+" letter word with "+e+" as the vowels and "+t+" as the consonants")}var r=false;var i="";var s="";for(var o=0;o<n;o++){if(r){s=e[Math.floor(Math.random()*e.length)]}else{s=t[Math.floor(Math.random()*t.length)]}i+=s;r=!r}return i}function ClearVowels(){var e=$("#vowels_container .letter-toggle");DeactivateTheseToggles(e)}function ClearConsonants(){var e=$("#consonants_outer_container .letter-toggle");DeactivateTheseToggles(e)}function DeactivateTheseToggles(e){e.each(function(){$(this).removeClass("active")})}function AllVowels(){var e=$("#vowels_container .letter-toggle");ActivateTheseToggles(e)}function AllConsonants(){var e=$("#consonants_outer_container .letter-toggle");ActivateTheseToggles(e)}function ActivateTheseToggles(e){e.each(function(){$(this).addClass("active")})}function SelectRandomLength(){var e=Math.floor(Math.random()*7+3);$("#length_slider").slider("setValue",e);$("#length_slider").val(e)}var mDebug=false;var mVowels=["A","E","I","O","U","Y"];var mConsonants=["B","C","D","F","G","H","J","K","L","M","N","P","Q","R","S","T","V","W","X","Y","Z"];$(function(){$("#length_slider").slider();PopulateLetterToggles();SelectRandomToggles();GenerateWord();$("#generate_btn").bind("click",function(){GenerateWord()});$("#clear_vowels").bind("click",function(){ClearVowels()});$("#clear_consonants").bind("click",function(){ClearConsonants()});$("#all_vowels").bind("click",function(){AllVowels()});$("#all_consonants").bind("click",function(){AllConsonants()});$("#random_btn").bind("click",function(){SelectRandomToggles();SelectRandomLength();GenerateWord()})})
